body.script:
//
(function() {
  // Update reading progress on scroll
  function updateProgress() {
    var docHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
    var scrolled = (window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0);
    var percentage = docHeight > 0 ? (scrolled / docHeight) * 100 : 0;
    var bar = document.getElementById('reading-progress');
    if (bar) bar.style.width = percentage + '%';
  }
  window.addEventListener('scroll', updateProgress);
  document.addEventListener('DOMContentLoaded', function() {
    // Reading time calculation for each post
    var posts = document.querySelectorAll('.post');
    posts.forEach(function(post) {
      var body = post.querySelector('.post-body');
      var timeSpan = post.querySelector('.reading-time');
      if (body && timeSpan) {
        var text = body.innerText || body.textContent || '';
        var words = text.trim().split(/\s+/).length;
        var minutes = Math.max(1, Math.ceil(words / 200));
        timeSpan.textContent = minutes + ' min read';
      }
    });
    // Dark mode toggle
    var darkToggle = document.getElementById('dark-toggle');
    if (darkToggle) {
      darkToggle.addEventListener('click', function() {
        document.body.classList.toggle('dark-mode');
      });
    }
    // Focus mode toggle
    var focusToggle = document.getElementById('focus-toggle');
    if (focusToggle) {
      focusToggle.addEventListener('click', function() {
        document.body.classList.toggle('focus-mode');
      });
    }
    // Back to top button functionality
    var backToTop = document.getElementById('back-to-top');
    if (backToTop) {
      window.addEventListener('scroll', function() {
        if (window.pageYOffset > 400) {
          backToTop.style.display = 'block';
        } else {
          backToTop.style.display = 'none';
        }
      });
      backToTop.addEventListener('click', function() {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      });
    }
  });
})();
//
