body.script:
//
(function() {
  document.addEventListener('DOMContentLoaded', function() {
    // Dark mode toggle
    var darkToggle = document.getElementById('dark-toggle');
    if (darkToggle) {
      darkToggle.addEventListener('click', function() {
        document.body.classList.toggle('dark-mode');
      });
    }
    // Back to top button
    var backToTop = document.getElementById('back-to-top');
    if (backToTop) {
      window.addEventListener('scroll', function() {
        if (window.pageYOffset > 400) {
          backToTop.style.display = 'block';
        } else {
          backToTop.style.display = 'none';
        }
      });
      backToTop.addEventListener('click', function() {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      });
    }
    // Category filtering
    var filterBar = document.getElementById('portfolio-filter');
    if (filterBar) {
      var filterLinks = filterBar.querySelectorAll('a');
      filterLinks.forEach(function(link) {
        link.addEventListener('click', function(e) {
          e.preventDefault();
          var href = this.getAttribute('href') || '';
          var category = href.replace('#','').toLowerCase();
          // Set active class
          filterLinks.forEach(function(l) { l.classList.remove('active'); });
          this.classList.add('active');
          // Filter posts
          var posts = document.querySelectorAll('.post');
          posts.forEach(function(post) {
            var pCat = post.getAttribute('data-category');
            if (!pCat) {
              var labelEl = post.querySelector('.post-labels a');
              pCat = labelEl ? labelEl.textContent.trim().toLowerCase() : '';
              post.setAttribute('data-category', pCat);
            }
            if (category === 'all' || category === '' || pCat === category) {
              post.style.display = '';
            } else {
              post.style.display = 'none';
            }
          });
        });
      });
    }
    // Lightbox functionality
    var lightbox = document.getElementById('lightbox');
    var lightboxImg = document.getElementById('lightbox-img');
    var main = document.getElementById('main');
    if (lightbox && lightboxImg && main) {
      main.addEventListener('click', function(e) {
        var target = e.target;
        if (target && target.tagName.toLowerCase() === 'img') {
          lightboxImg.src = target.src;
          lightbox.style.display = 'flex';
        }
      });
      lightbox.addEventListener('click', function() {
        lightbox.style.display = 'none';
      });
      document.addEventListener('keydown', function(e) {
        if (e.key === 'Escape') {
          lightbox.style.display = 'none';
        }
      });
    }
  });
})();
//
