body.script:
//
(function() {
  // Update reading progress on scroll
  function updateProgress() {
    var docHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
    var scrolled = (window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0);
    var percentage = docHeight > 0 ? (scrolled / docHeight) * 100 : 0;
    var bar = document.getElementById('reading-progress');
    if (bar) bar.style.width = percentage + '%';
  }
  window.addEventListener('scroll', updateProgress);
  document.addEventListener('DOMContentLoaded', function() {
    // Reading time calculation for each post
    var posts = document.querySelectorAll('.post');
    posts.forEach(function(post) {
      var body = post.querySelector('.post-body');
      var timeSpan = post.querySelector('.reading-time');
      if (body && timeSpan) {
        var text = body.innerText || body.textContent || '';
        var words = text.trim().split(/\s+/).length;
        var minutes = Math.max(1, Math.ceil(words / 200));
        timeSpan.textContent = minutes + ' min read';
      }
    });
    // Dark mode toggle
    var darkToggle = document.getElementById('dark-toggle');
    if (darkToggle) {
      darkToggle.addEventListener('click', function() {
        document.body.classList.toggle('dark-mode');
      });
    }
    // Back to top functionality
    var backToTop = document.getElementById('back-to-top');
    if (backToTop) {
      window.addEventListener('scroll', function() {
        if (window.pageYOffset > 400) {
          backToTop.style.display = 'block';
        } else {
          backToTop.style.display = 'none';
        }
      });
      backToTop.addEventListener('click', function() {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      });
    }
    // Subject filter interactions
    var filterBar = document.getElementById('subject-filter');
    if (filterBar) {
      var filterLinks = filterBar.querySelectorAll('a');
      filterLinks.forEach(function(link) {
        link.addEventListener('click', function(e) {
          e.preventDefault();
          // Determine subject key from href or data attribute
          var href = this.getAttribute('href') || '';
          var subject = href.replace('#','').toLowerCase();
          if (!subject) subject = this.getAttribute('data-subject') || '';
          // Update active class
          filterLinks.forEach(function(l) { l.classList.remove('active'); });
          this.classList.add('active');
          // Filter posts on home page
          var postsList = document.querySelectorAll('.post');
          postsList.forEach(function(post) {
            var pSubject = post.getAttribute('data-subject');
            if (!pSubject) {
              var labelEl = post.querySelector('.post-labels a');
              pSubject = labelEl ? labelEl.textContent.trim().toLowerCase() : '';
              post.setAttribute('data-subject', pSubject);
            }
            if (subject === 'all' || subject === '' || pSubject === subject) {
              post.style.display = '';
            } else {
              post.style.display = 'none';
            }
          });
        });
      });
    }
    // Auto-generate table of contents in posts
    var articlePosts = document.querySelectorAll('.post');
    articlePosts.forEach(function(post) {
      var body = post.querySelector('.post-body');
      if (!body) return;
      var headings = body.querySelectorAll('h2, h3, h4');
      if (headings.length > 2) {
        var toc = document.createElement('div');
        toc.id = 'toc-container';
        var title = document.createElement('h3');
        title.textContent = 'Contents';
        toc.appendChild(title);
        var ul = document.createElement('ul');
        toc.appendChild(ul);
        headings.forEach(function(h, idx) {
          if (!h.id) {
            var slug = h.textContent.toLowerCase().trim().replace(/[^a-z0-9]+/g, '-');
            h.id = slug || ('section-' + idx);
          }
          var li = document.createElement('li');
          var a = document.createElement('a');
          a.href = '#' + h.id;
          a.textContent = h.textContent;
          li.appendChild(a);
          ul.appendChild(li);
        });
        body.insertBefore(toc, body.firstChild);
      }
    });
  });
})();
