body.script:
// 
(function(){
  // Theme toggle
  var key = 'bz_theme';
  var root = document.documentElement;
  var btn = document.getElementById('bzThemeToggle');

  function applyTheme(t){
    if(!t){ root.removeAttribute('data-bz-theme'); return; }
    root.setAttribute('data-bz-theme', t);
  }

  try{
    var saved = localStorage.getItem(key);
    if(saved){ applyTheme(saved); }
  }catch(e){}

  if(btn){
    btn.addEventListener('click', function(){
      var current = root.getAttribute('data-bz-theme');
      var next = (current === 'dark') ? 'light' : 'dark';
      applyTheme(next);
      try{ localStorage.setItem(key, next); }catch(e){}
    });
  }

  // Soft badge enforcement: if removed, re-add quietly (no blocking screens)
  function ensureBadge(){
    var id = 'bzStudioBadge';
    var badge = document.getElementById(id);
    if(badge && badge.offsetParent !== null) return;

    var a = document.createElement('a');
    a.id = id;
    a.href = 'https://www.blogger.com';
    a.target = '_blank';
    a.rel = 'noopener';
    a.textContent = 'âš¡ Built with Bloggerz Studio';
    a.style.cssText =
      'position:fixed;right:16px;bottom:16px;z-index:99999;' +
      'background:var(--bz-accent);color:var(--bz-accent-ink);' +
      'border:var(--bz-border-w) solid var(--bz-border);' +
      'box-shadow:var(--bz-shadow);' +
      'padding:10px 14px;border-radius:14px;' +
      'font-family:var(--bz-font-head);text-transform:uppercase;' +
      'letter-spacing:.4px;font-size:12px;text-decoration:none;';
    document.body.appendChild(a);
  }

  setTimeout(ensureBadge, 2500);
  setInterval(ensureBadge, 15000);
})();
//
